import { useState } from 'react';
import classes from './UploadPage.module.css';
import { postInDb } from './Api';

const UploadPage = (props) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [imageFile, setImageFile] = useState(null);
    const [videoFile, setVideoFile] = useState(null);
    const [upload , setUpload] = useState("not upload");
    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = {
            title: title,
            description: description,
            imageFile: imageFile,
            videoFile: videoFile
        };
        await postInDb({ formData, setRerender: props.setRender });
        setDescription("");
        setImageFile(null);
        setVideoFile(null);
        setTitle("");
    };

    return (
        <div className={classes.mainContainer}>
            <div className={classes.innerContainer}>
                <div className={classes.inputTextContainer}>
                    Neon Flake
                </div>
                <input
                    type="text"
                    placeholder="Title"
                    maxLength="50"
                    required
                    className={classes.inputField}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <textarea
                    placeholder="Description"
                    maxLength="200"
                    required
                    className={classes.textareaField}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <input
                    type="file"
                    accept="image/jpeg,image/png"
                    required
                    className={classes.imageInput}
                    onChange={(e) => setImageFile(e.target.files[0])}
                />
                <input
                    type="file"
                    accept="video/mpg,video/avi,video/mp4"
                    required
                    className={classes.videoInput}
                    onChange={(e) => setVideoFile(e.target.files[0])}
                />
                <button className={classes.buttonInput} onClick={handleSubmit}>
                    Upload
                </button>
            </div>
        </div>
    );
};

export default UploadPage;
