import axios from "axios"

const postImageToCloud = (formData) => {
    const passKey = "Neonflake";
    const cloud = "CloudinaryReshwanth";
    const imageUpload = new FormData();
    imageUpload.append("upload_preset",passKey);
    imageUpload.append("file",formData.imageFile);

    axios.post(`https://api.cloudinary.com/v1_1/${cloud}/image/upload`,imageUpload)
    .then(res => (console.log(res.data)))
}

const postVideoToCloud = (formData) => {
    const passKey = "Neonflake";
    const cloud = "CloudinaryReshwanth";
    const videoUpload = new FormData();
    videoUpload.append("upload_preset",passKey);
    videoUpload.append("file",formData.videoFile);
    axios.post(`https://api.cloudinary.com/v1_1/${cloud}/video/upload`,videoUpload)
    .then(res => {
        console.log(res.data);
        return(res.data)}
    )
}

export const postInDb = (formData) => {
    const imageFile = postImageToCloud(formData);
    const videoFile = postVideoToCloud(formData);
    console.log(videoFile);
    console.log(imageFile);
