import React, { useState } from 'react';
import UploadPage from './UploadPage';
import WebPage2 from './WebPage2';
import WebPage3 from './WebPage3';
import classes from './App.module.css';

const App = () => {
  const [render, setRender] = useState(false);
  const [selectedVideo, setSelectedVideo] = useState(null); // State to manage the selected video

  return (
    <div className={classes.mainContainer}>
      <UploadPage setRender={setRender} />
      <WebPage2 render={render} onThumbnailClick={setSelectedVideo} /> {/* Pass the function to WebPage2 */}
      <WebPage3 video={selectedVideo} /> {/* Pass the selected video to WebPage3 */}
    </div>
  );
};

export default App;
