// UploadPage.jsx
import React, { useState, useRef } from 'react';
import styles from './UploadPage.module.css';
import ModalContent from './ModalContent';

const UploadPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [thumbnail, setThumbnail] = useState(null);
  const [video, setVideo] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const fileInputRef = useRef(null);
  const videoInputRef = useRef(null);

  const handleThumbnailChange = (e) => {
    setThumbnail(e.target.files[0]);
  };

  const handleVideoChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic, such as sending data to a server
    console.log('Title:', title);
    console.log('Description:', description);
    console.log('Thumbnail:', thumbnail);
    console.log('Video:', video);
  };

  const openModal = (inputType) => {
    fileInputRef.current = inputType;
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleFileSelect = () => {
    if (fileInputRef.current === 'image') {
      document.getElementById('image-input').click();
    } else if (fileInputRef.current === 'video') {
      document.getElementById('video-input').click();
    }
  };

  return (
    <div className={styles.container}>
      <h1>Upload Page</h1>
      <form onSubmit={handleSubmit} className={styles.uploadForm}>
        <input
          type="text"
          placeholder="Title"
          maxLength="50"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          className={styles.inputField}
        />
        <textarea
          placeholder="Description"
          maxLength="200"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          className={styles.textareaField}
        />
        <div className={styles.fileInputFake} onClick={() => openModal('image')}>Select Thumbnail</div>
        <div className={styles.fileInputFake} onClick={() => openModal('video')}>Select Video</div>
        <button type="submit" className={styles.submitButton}>Upload</button>
      </form>
      {isModalOpen && <ModalContent onClose={closeModal} onFileSelect={handleFileSelect} />}
      <input
        type="file"
        id="image-input"
        style={{ display: 'none' }}
        onChange={handleThumbnailChange}
        accept="image/jpeg,image/png"
      />
      <input
        type="file"
        id="video-input"
        style={{ display: 'none' }}
        onChange={handleVideoChange}
        accept="video/mpg,video/avi,video/mp4"
      />
    </div>
  );
};

export default UploadPage;
