import { useEffect, useState } from 'react';
import OneThumbnail from './OneThumbnail';
import classes from './WebPage2.module.css';
import { getInDb } from './Api';

const WebPage2 = () => {
    const [allFolders, setFolders] = useState([]);  // Initialize as an empty array

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getInDb(); // Fetch data
                if (Array.isArray(data)) {
                    setFolders(data); // Set data to state
                } else {
                    console.error("Received data is not an array:", data);
                    setFolders([]); // Set an empty array in case of unexpected data
                }
            } catch (error) {
                console.error("Error fetching data:", error);
                setFolders([]); // Set an empty array on error
            }
        };
        fetchData();
    }, []);

    return (
        <div className={classes.mainContainer}>
            <div className={classes.innerContainer}>
                {allFolders.length > 0 ? (
                    allFolders.map((oneItem, index) => (
                        <OneThumbnail 
                            key={index}
                            item={oneItem}
                        />
                    ))
                ) : (
                    <p>No items found</p>  // Optional: Show a message when there are no items
                )}
            </div>
        </div>
    );
};

export default WebPage2;
