// UploadPage.jsx
import React, { useState, useRef } from 'react';
import './UploadPage.css';
import ModalContent from './ModalContent';

const UploadPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [thumbnail, setThumbnail] = useState(null);
  const [video, setVideo] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const fileInputRef = useRef(null);
  const videoInputRef = useRef(null);

  const handleThumbnailChange = (e) => {
    setThumbnail(e.target.files[0]);
  };

  const handleVideoChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic, such as sending data to a server
    console.log('Title:', title);
    console.log('Description:', description);
    console.log('Thumbnail:', thumbnail);
    console.log('Video:', video);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleFileSelect = () => {
    fileInputRef.current.click();
  };

  const handleVideoFileSelect = () => {
    videoInputRef.current.click();
  };

  return (
    <div className="container">
      <h1>Upload Page</h1>
      <form onSubmit={handleSubmit} className="upload-form">
        <input
          type="text"
          placeholder="Title"
          maxLength="50"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          className="input-field"
          onClick={openModal}
        />
        <textarea
          placeholder="Description"
          maxLength="200"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          className="textarea-field"
          onClick={openModal}
        />
        <div className="file-input-fake" onClick={() => {
          setIsModalOpen(true);
          fileInputRef.current = 'image';
        }}>Select Thumbnail</div>
        <div className="file-input-fake" onClick={() => {
          setIsModalOpen(true);
          videoInputRef.current = 'video';
        }}>Select Video</div>
        <button type="submit" className="submit-button">Upload</button>
      </form>
      {isModalOpen && <ModalContent onClose={closeModal} onFileSelect={() => {
        if (fileInputRef.current === 'image') {
          handleFileSelect();
        } else if (videoInputRef.current === 'video') {
          handleVideoFileSelect();
        }
      }} />}
      <input
        type="file"
        style={{ display: 'none' }}
        ref={fileInputRef}
        onChange={handleThumbnailChange}
        accept="image/jpeg,image/png"
      />
      <input
        type="file"
        style={{ display: 'none' }}
        ref={videoInputRef}
        onChange={handleVideoChange}
        accept="video/mpg,video/avi,video/mp4"
      />
    </div>
  );
};

export default UploadPage;
